package main

import (
	"gogc/src/common/db"
	sw "gogc/src/station/handler"
	"log"
	"os"
)

func main() {
	log.Printf("Server started")

	// Init
	router := sw.NewRouter()
	db.Init()

	port := os.Getenv("ENV_DATABASE_PORT")
	if port == "" {
		port = "8888"
	}
	port = ":" + port

	log.Fatal(router.Run(port))
}
