package main

import (
	sw "gogc/src/udr/DataRepository/handler"
	"gogc/src/udr/DataRepository/scenario"
	"log"
	"os"
)

func main() {
	log.Printf("Server started")

	// Init
	scenario.Init()
	router := sw.NewRouter()

	port := os.Getenv("ENV_UDR_DATAREPOSITORY_PORT")
	if port == "" {
		port = "8481"
	}
	port = ":" + port

	log.Fatal(router.Run(port))
}
